        transData.setInput("");
        transData.setCode("1");
        transDatas.add(transData);
        pPosInvokeContractInput.setTransDatas(transDatas);
        TransactionUtil.getVirtualTxList(block, transaction, pPosInvokeContractInput, newAddressCache);

        List<Log> logs = new ArrayList<>();
        Log log = new Log();
        log.setData("0xf84e30b84bf849f847b840362003c50ed3a523cdede37a001803b8f0fed27cb402b3d6127a1a96661ec202318f68f4c76d9b0bfbabfd551a178d4335eaeaa9b7981a4df30dfc8c0bfe3384830f424064");
        logs.add(log);
        try {
            TransactionUtil.getDelegateReward(logs);
        } catch (Exception e) {

        }

        com.platon.browser.elasticsearch.dto.Transaction dtoTransaction = DtoTransactionUtil.newDtoTransaction();
        ComplementInfo ci = new ComplementInfo();
        dtoTransaction.setInput("");
        TransactionUtil.resolveInnerContractInvokeTxComplementInfo(dtoTransaction, logs, ci);

